import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft, faTemperatureHigh, faTemperatureLow } from '@fortawesome/free-solid-svg-icons';

const ForecastPage = () => {
  const location = useLocation();
  const { forecastData } = location.state;
  const navigate = useNavigate();
  const [expandedCard, setExpandedCard] = useState(null);
  const [hoveredCard, setHoveredCard] = useState(null);

  const formatDateTime = (dateTime) => {
    const date = new Date(dateTime);
    const options = {
      weekday: 'short',
      month: 'numeric',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
      hour12: true,
      year: 'numeric',
      timeZone: forecastData.timezone,
    };
    const formattedDate = date.toLocaleString('en-US', options);
    const [dayMonth, time] = formattedDate.split(',');
    return { dayMonth, time };
  };

  const navigateGoBack = () => {
    navigate(-1);
  };

  const handleCardClick = (index) => {
    setExpandedCard(expandedCard === index ? null : index);
  };

  const handleCardMouseEnter = (index) => {
    setHoveredCard(index);
  };
  
  const handleCardMouseLeave = () => {
    setHoveredCard(null);
  };

  return (
    <div className="forecast-page">
      <div className="forecast-container">
        <FontAwesomeIcon icon={faArrowLeft} className="back-btn" onClick={navigateGoBack} />
        {forecastData && forecastData.daily ? (
          forecastData.daily.time.map((time, index) => {
            const { dayMonth, time: formattedTime } = formatDateTime(day.time);
            const isHovered = hoveredCard === index;
            const isExpanded = expandedCard === index;
            return (
              <div
                className={`forecast-card ${isHovered ? 'hovered' : ''} ${isExpanded ? 'expanded' : ''}`}
                key={index}
                onMouseEnter={() => handleCardMouseEnter(index)}
                onMouseLeave={handleCardMouseLeave}
                onClick={() => handleCardClick(index)}
              >
                <h3 id="day-month">{dayMonth}</h3>
                {isHovered && !isExpanded && (
                  <p>
                    <span className="subtext">{formattedTime}</span> <br />
                    Max: {day.temperature_2m_max}째C <FontAwesomeIcon icon={faTemperatureHigh} />
                    <br />
                    Min: {day.temperature_2m_min}째C <FontAwesomeIcon icon={faTemperatureLow} />
                  </p>
                )}
                {isExpanded && (
                  <div className="expanded-details">
                    <p>
                      <span className="subtext">{formattedTime}</span> <br />
                      Max: {day.temperature_2m_max}째C <FontAwesomeIcon icon={faTemperatureHigh} />
                      <br />
                      Min: {day.temperature_2m_min}째C <FontAwesomeIcon icon={faTemperatureLow} />
                      <br />
                      UV Index: {day.uv_index_max}
                      <br />
                      Precipitation: {day.precipitation_sum}mm
                      <br />
                      Rain: {day.rain_sum}mm
                      <br />
                    </p>
                  </div>
                )}
              </div>
            );
          })
        ) : (
          <div>No forecast data available</div>
        )}
      </div>
    </div>
  );
}
export default ForecastPage;
