import logo from './logo.svg';
import './App.css';
import React, { useState} from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import WeatherApp from './components/WeatherApp';
import ForecastPage from './components/ForecastPage'; // Import the ForecastPage component


const App = () => {
  const [locationInfo, setLocationInfo] = useState('');
  const navigate = useNavigate();

  const handleLocationSubmit = (location) => {
    setLocationInfo(location);
    navigate('/forecast', {state: {locationInfo: location}});
  };

  return (
    <div className="App">
      <header className="App-header">
        <Router>
          <Routes>
            <Route path="/" element={<WeatherApp/>} />
            <Route path="/forecast" element={<ForecastPage locationInfo={locationInfo} />} /> {/* Add a route for the ForecastPage component */}
          </Routes>
        </Router>
      </header>
    </div>
  );
};

export default App;
