import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft, faTemperatureHigh, faTemperatureLow, faCloudRain, faCloudSun, faCloudSunRain, faCloudShowersHeavy, faSun} from '@fortawesome/free-solid-svg-icons';

const ForecastPage = (query) => {
  const location = useLocation();
  const { forecastData } = location.state;
  const navigate = useNavigate();
  const [expandedCard, setExpandedCard] = useState(null);
  const [hoveredCard, setHoveredCard] = useState(null);


  // Assuming forecastData.daily.time is an array of timestamps representing days of the week

// Function to reorder the forecast data array
  const reorderForecastData = (data) => {
    // Convert the timestamp to day index (0 for Monday, 1 for Tuesday, ..., 6 for Sunday)
    const dayIndex = (timestamp) => new Date(timestamp).getDay();

    // Reorder the forecast data array
    const reorderedData = [...data].sort((a, b) => {
      const dayIndexA = dayIndex(a);
      const dayIndexB = dayIndex(b);
      return dayIndexA - dayIndexB;
    });

    return reorderedData;
  };

  // Inside the component
  const reorderedForecastData = forecastData && forecastData.daily && forecastData.daily.time
    ? reorderForecastData(forecastData.daily.time)
    : [];

  // Render forecast cards based on the reordered forecast data
  {reorderedForecastData.map((time, index) => {
    // Your rendering logic here
  })}


  const formatDateTime = (dateTime) => {
    const date = new Date(dateTime);
    const options = {
      weekday: 'short',
      month: 'numeric',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
      hour12: true,
      year: 'numeric',
      timeZone: forecastData.timezone,
    };
    const formattedDate = date.toLocaleString('en-US', options);
    const [dayMonth, time] = formattedDate.split(',');
    return { dayMonth, time };
  };

  const navigateGoBack = () => {
    navigate(-1);
  };

  const handleCardClick = (index) => {
    setExpandedCard(expandedCard === index ? null : index);
  };

  const handleCardMouseEnter = (index) => {
    setHoveredCard(index);
  };
  
  const handleCardMouseLeave = () => {
    setHoveredCard(null);
  };

  return (
    <div className="forecast-page">
      <div className="forecast-container" style={{ backgroundColor: 'rgba(255, 255, 255, 0.7)' }}>
        <h1>Showing weather for {query}</h1>
        <FontAwesomeIcon icon={faArrowLeft} className="back-btn" onClick={navigateGoBack} />
        {forecastData && forecastData.daily && forecastData.daily.time && forecastData.daily.temperature_2m_max && forecastData.daily.temperature_2m_min ? (
          forecastData.daily.time.map((time, index) => {
            const { dayMonth, time: formattedTime } = formatDateTime(time);
            const isHovered = hoveredCard === index;
            const isExpanded = expandedCard === index;
            return (
              <div
                className={`forecast-card ${isHovered ? 'hovered' : ''} ${isExpanded ? 'expanded' : ''}`}
                key={index}
                onMouseEnter={() => setHoveredCard(index)}
                onMouseLeave={handleCardMouseLeave}
                onClick={() => handleCardClick(index)}
              >
                <h3 id='day-month'>{dayMonth}</h3>
                {isHovered && !isExpanded && (
                  <p>
                    <span className="subtext">{formattedTime}</span> <br />
                    Max: {forecastData.daily.temperature_2m_max[index]}°C <FontAwesomeIcon icon={faTemperatureHigh} style={{color:'maroon'}}/><br />
                    Min: {forecastData.daily.temperature_2m_min[index]}°C <FontAwesomeIcon icon={faTemperatureLow}  style={{color:'blue'}}/>
                  </p>
                )}
                {isExpanded && (
                  <div className="expanded-details">
                    <p>
                    <span className="subtext">{formattedTime}</span> <br />
                    Max: {forecastData.daily.temperature_2m_max[index]}°C <FontAwesomeIcon icon={faTemperatureHigh} style={{color:'maroon'}} /><br />
                    Min: {forecastData.daily.temperature_2m_min[index]}°C <FontAwesomeIcon icon={faTemperatureLow} style={{color:'blue'}} /><br />
                    Precipitation Probability: {forecastData.daily.precipitation_probability_max[index]}% <FontAwesomeIcon icon={faCloudRain} style={{color:'purple'}}/><br />
                    Precipitation: {forecastData.daily.precipitation_sum[index]}mm <FontAwesomeIcon icon ={faCloudSunRain} /><br />
                    Rain: {forecastData.daily.rain_sum[index]}mm <FontAwesomeIcon icon={faCloudShowersHeavy} style={{color:'grey'}} /> <br />
                    UV Index: {forecastData.daily.uv_index_max[index]} <FontAwesomeIcon icon={faSun} style = {{color:'orange'}} /> <br />
                    UV Index (clear sky): {forecastData.daily.uv_index_clear_sky_max[index]} <FontAwesomeIcon icon={faCloudSun} style={{color:' yellow'}} /><br />
                  
                    </p>
                  </div>
                )}
              </div>
            );
          })
        ) : (
          <div>No forecast data available</div>
        )}
      </div>
    </div>
  );
};

export default ForecastPage;
