// ForecastPage.js
import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom'; // Import useLocation hook
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';


const ForecastPage = () => {
  const location = useLocation();
  const { forecastData } = location.state; // Get forecastData from location state
  const navigate = useNavigate();

  // Define formatDateTime function
  const formatDateTime = (dateTime) => {
    const date = new Date(dateTime);
    const options = {
      weekday: 'long',
      month: 'short',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
      hour12: true,
      year: 'numeric',
      timeZone: forecastData.timezone,
    };
    return date.toLocaleString('en-US', options);
  };

  const navigateGoBack = () => {
    navigate(-1); // Go back to the previous page
  };

  // Render forecast data
  return (
    <div>
      <h1>Forecast Page</h1>
      <FontAwesomeIcon icon = {faArrowLeft} onClick={navigateGoBack} />
      {/* Render forecast data here */}
      {forecastData && forecastData.daily && forecastData.daily.time && forecastData.daily.temperature_2m_max && forecastData.daily.temperature_2m_min ? (
        <div>
          <h2>Daily Forecast</h2>
          <table>
            <thead>
              <tr>
                {forecastData.daily.time.map((time, index) => (
                  <th key={index}>{formatDateTime(time)}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              <tr>
                {forecastData.daily.temperature_2m_max.map((maxTemp, index) => (
                  <td key={index}>
                    Max: {maxTemp}°C
                    <br />
                    Min: {forecastData.daily.temperature_2m_min[index]}°C
                  </td>
                ))}
              </tr>
            </tbody>
          </table>
        </div>
      ) : (
        <div>No forecast data available</div>
      )}
    </div>
  );
};

export default ForecastPage;
