import React, { useState, useEffect, useRef } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft, faArrowRight, faTemperatureHigh, faTemperatureLow, faCloudRain, faCloudSun, faCloudSunRain, faCloudShowersHeavy, faSun } from '@fortawesome/free-solid-svg-icons';

const ForecastPage = () => {
  const location = useLocation();
  const { forecastData } = location.state;
  const navigate = useNavigate();
  const [currentDayIndex, setCurrentDayIndex] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const [startX, setStartX] = useState(0);
  const forecastContainerRef = useRef(null);

  useEffect(() => {
    const today = new Date().getDate();
    setCurrentDayIndex(today - 1);
  }, []);

  const handleTouchStart = (event) => {
    setStartX(event.touches[0].clientX);
    setIsDragging(true);
  };

  const handleTouchMove = (event) => {
    if (!isDragging) return;
    const x = event.touches[0].clientX;
    const difference = startX - x;
    if (difference > 50) {
      handleNextDay();
      setIsDragging(false);
    } else if (difference < -50) {
      handlePreviousDay();
      setIsDragging(false);
    }
  };

  const handleTouchEnd = () => {
    setStartX(0);
    setIsDragging(false);
  };

  const handlePreviousDay = () => {
    if (currentDayIndex > 0) {
      setCurrentDayIndex(currentDayIndex - 1);
    }
  };

  const handleNextDay = () => {
    if (currentDayIndex < forecastData.daily.time.length - 1) {
      setCurrentDayIndex(currentDayIndex + 1);
    }
  };

  const renderForecastDays = () => {
    return forecastData.daily.time.map((time, index) => {
      const date = new Date(time);
      const day = date.getDate();
      const classNames = ['forecast-day'];
      if (index === currentDayIndex) classNames.push('current-day');
      return (
        <div className={classNames.join(' ')} key={index}>
          <h3>{day}</h3>
        </div>
      );
    });
  };

  const navigateGoBack = () => {
    navigate(-1);
  };

  return (
    <div className="forecast-page">
      <div className="forecast-container" style={{ backgroundColor: 'rgba(255, 255, 255, 0.7)' }}>
        <FontAwesomeIcon icon={faArrowLeft} className="back-btn" onClick={navigateGoBack} />
        <div className="forecast-header">
          <FontAwesomeIcon icon={faArrowLeft} className="navigate-btn" onClick={handlePreviousDay} />
          <h2>{forecastData.daily.time[currentDayIndex]}</h2>
          <FontAwesomeIcon icon={faArrowRight} className="navigate-btn" onClick={handleNextDay} />
        </div>
        <div
          className="forecast-days-container"
          onTouchStart={handleTouchStart}
          onTouchMove={handleTouchMove}
          onTouchEnd={handleTouchEnd}
          ref={forecastContainerRef}
        >
          {renderForecastDays()}
        </div>
      </div>
    </div>
  );
};

export default ForecastPage;
