import logo from './logo.svg';
import './App.css';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import WeatherApp from './components/WeatherApp';
import ForecastPage from './components/ForecastPage'; // Import the ForecastPage component


const App = () => {

  return (
    <div className="App">
      <header className="App-header">
        <Router>
          <Routes>
            <Route path="/" element={<WeatherApp/>} />
            <Route path="/forecast/:location" element={<ForecastPage />} /> {/* Add a route for the ForecastPage component */}
          </Routes>
        </Router>
      </header>
    </div>
  );
};

export default App;
