import React, { useState } from 'react';
import LocationForm from './LocationForm';
import { useNavigate } from 'react-router-dom'; // Import useHistory hook

const WeatherApp = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate(); // Initialize useNavigate\
  <FontAwesomeIcon icon={faCloud} />

  const handleSubmitLocation = async (location) => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(
        `https://geocoding-api.open-meteo.com/v1/search?name=${encodeURIComponent(location)}&count=1&language=en&format=json`
      );
      const data = await response.json();
      if (data && data.results && data.results.length > 0) {
        const { latitude, longitude, timezone } = data.results[0];
        const forecastResponse = await fetch(
          `https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&hourly=&daily=temperature_2m_max,temperature_2m_min&timezone=${timezone}`
        );
        const forecastData = await forecastResponse.json();
        setWeatherData(forecastData);
        // Navigate to forecast page
        navigate('/forecast', { state: { forecastData } });
      } else {
        throw new Error('Location not found');
      }
    } catch (error) {
      console.error('Error fetching weather data:', error);
      setError('Failed to fetch weather data. Please try again.');
    } finally {
      setLoading(false);
    }
  };
  return (
    <div className="App">
      <h1 className="main-heading">Weather</h1>
      <h2 className="forecast">Forecast</h2>
      <LocationForm onSubmit={handleSubmitLocation} />
      {loading && <p>Loading...</p>}
      {error && <p>{error}</p>}
    </div>
  );
};

export default WeatherApp;
