import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';
import './ForecastPage.css'; // Import CSS file for styling

const ForecastPage = () => {
  const location = useLocation();
  const { forecastData } = location.state;
  const navigate = useNavigate();

  const formatDateTime = (dateTime) => {
    const date = new Date(dateTime);
    const options = {
      weekday: 'long',
      month: 'short',
      day: 'numeric',
      hour: 'numeric',
      minute: 'numeric',
      hour12: true,
      year: 'numeric',
      timeZone: forecastData.timezone,
    };
    return date.toLocaleString('en-US', options);
  };

  const navigateGoBack = () => {
    navigate(-1);
  };

  return (
    <div className="forecast-page">
      <FontAwesomeIcon icon={faArrowLeft} onClick={navigateGoBack} />
      <h1 className="main-heading">Weather Forecast</h1>
      <div className="forecast-container">
        {forecastData && forecastData.daily && forecastData.daily.time && forecastData.daily.temperature_2m_max && forecastData.daily.temperature_2m_min ? (
          forecastData.daily.time.map((time, index) => (
            <div className="forecast-card" key={index}>
              <h3>{formatDateTime(time)}</h3>
              <p>
                Max: {forecastData.daily.temperature_2m_max[index]}°C<br />
                Min: {forecastData.daily.temperature_2m_min[index]}°C
              </p>
            </div>
          ))
        ) : (
          <div>No forecast data available</div>
        )}
      </div>
    </div>
  );
};

export default ForecastPage;
